<script>
import MiniCart from '@/components/pages/MiniCart.vue';

export default {
	name: 'app-header',
	components: {
		MiniCart,
	},
	data() {
		return {
			items: [
				{ title: 'Shop', name: 'shop', id: 0 },
				{ title: 'Blog', name: 'blog', id: 1 },
				{ title: 'Our-story', name: 'our-story', id: 2 },
			],
			itemsL: [
				{ name: 'Ўзбекча', value: 'uz', id: 0 },
				{ name: "O'zbekcha", value: 'uzb', id: 1 },
				{ name: 'Русский', value: 'ru', id: 2 },
			],
			activeItem: 0,
			mobile_items: [
				{ title: 'Home', link: 'home', id: 0 },
				{ title: 'Shop', link: 'shop', id: 1 },
				{ title: 'About', link: 'shop', id: 2 },
				{ title: 'Blog', link: 'blog', id: 3 },
				{ title: 'Help', link: 'shop', id: 4 },
				{ title: 'Contact', link: 'contactUs', id: 5 },
				{ title: 'Search', link: 'shop', id: 6 },
			],
			btn_active: false,
			menu_active: false,
			cart_active: false,
			outerVisible: false,
			language: null,
		};
	},
	methods: {
		changeLanguage() {
			localStorage.setItem('lang', this.language);
			window.location.reload();
		},
		close_cart() {
			this.cart_active = !this.cart_active;
		},
		goHome(name) {
			if (name === 'home') {
				this.$router.push('/');
			}
		},
		isActive(i) {
			this.activeItem = i;
		},
	},
	computed: {},
	created() {
		if (localStorage.getItem('lang' == null)) {
			localStorage.setItem('lang', 'uz');
		}
		this.language = localStorage.getItem('lang');
	},
	mounted() {},
};
</script>

